// pages/HomePage.tsx
import React, { useState, useEffect } from 'react';
import './HomePage.css'; // Импортируем файл стилей

const HomePage: React.FC = () => {
  const [sortConfig, setSortConfig] = useState({ key: 'name', direction: 'ascending' });

  const rows = [
    { name: 'Иван Иванов', room: '101' },
    { name: 'Петр Петров', room: '102' },
    // добавьте больше студентов здесь...
  ];

  useEffect(() => {
    const sortArray = type => {
      const types = {
        name: 'name',
        room: 'room',
      };
      const sortProperty = types[type];
      const sorted = [...rows].sort((a, b) => a[sortProperty].localeCompare(b[sortProperty]));
      if (sortConfig.direction === 'descending') sorted.reverse();
      return sorted;
    };

    if (sortConfig.key) {
      const sortedData = sortArray(sortConfig.key);
      rows = sortedData;
    }
  }, [sortConfig]);

  const requestSort = key => {
    let direction = 'ascending';
    if (sortConfig.key === key && sortConfig.direction === 'ascending') {
      direction = 'descending';
    }
    setSortConfig({ key, direction });
  };

  return (
    <div className="home-page">
      <h1 className="home-page__title">Добро пожаловать на главную страницу</h1>
      <p className="home-page__info">
        Жители этого общажного ада.
      </p>
      <table>
        <thead>
          <tr>
            <th onClick={() => requestSort('name')}>Имя</th>
            <th onClick={() => requestSort('room')}>Комната</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row, index) => (
            <tr key={index}>
              <td>{row.name}</td>
              <td>{row.room}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default HomePage;
